LIMIT #{limit} OFFSET #{offset}

mapper.delete:
DELETE FROM image_tag 

record_no = #{recordNo}
AND image_tag_type= #{imageTagType}

mapper.select:
SELECT 
	COUNT(*) AS checkpoint_count, 
	(SELECT 
	DATE_FORMAT(checkpoint_date, '%Y-%m-%d %H:%i:%s') AS checkpoint_date 
	FROM record 
	WHERE checkpoint_address=#{checkpointAddress:VARCHAR} 
	AND record_user_id=#{recordUserId:VARCHAR}
	AND timecapsule_type=0 
	ORDER BY checkpoint_date DESC 
	LIMIT 1) AS checkpoint_date 
FROM record 
WHERE checkpoint_address=#{checkpointAddress:VARCHAR}  
AND record_user_id=#{recordUserId:VARCHAR} 
AND timecapsule_type=0 
GROUP BY checkpoint_address=#{checkpointAddress:VARCHAR}

mapper.update:
UPDATE keyword_data
SET KEYWORD_USER_ID = #{keywordUserId}, KEYWORD = #{keyword}, KEYWORD_COUNT = #{keywordCount}
WHERE KEYWORD_NO = #{keywordNo}

mapper.sql:
SELECT RECORD_NO, RECORD_USER_ID, RECORD_TITLE, RECORD_TEXT, CATEGORY_NO, DATE_FORMAT(r.shared_date, '%Y-%m-%d %H:%i:%s') AS shared_date, UPDATE_COUNT, (SELECT nickname FROM users u WHERE u.USER_ID = r.RECORD_USER_ID) AS nickname, (SELECT profile_image_name FROM users u WHERE u.USER_ID = r.RECORD_USER_ID) AS profile_image_name, CASE WHEN (SELECT subscription_end_date FROM subscription s WHERE s.USER_ID = r.RECORD_USER_ID ORDER BY subscription_end_date DESC LIMIT 1) > CURRENT_DATE THEN 1 ELSE 0 END AS subscription_end_date, (SELECT IMAGE_TAG_TYPE FROM image_tag i WHERE i.RECORD_NO = r.RECORD_NO AND IMAGE_TAG_TYPE = 1 LIMIT 1) AS IMAGE_TAG_TYPE, (SELECT IMAGE_TAG_TEXT FROM image_tag i WHERE i.RECORD_NO = r.RECORD_NO AND IMAGE_TAG_TYPE = 1 LIMIT 1) AS IMAGE_TAG_TEXT, (SELECT category_name FROM category c WHERE c.CATEGORY_NO = r.CATEGORY_NO) AS category_name, (SELECT category_imoji FROM category c WHERE c.CATEGORY_NO = r.CATEGORY_NO) AS category_imoji, (SELECT COUNT(*) FROM reply p WHERE p.RECORD_NO = r.RECORD_NO) AS reply_count, (SELECT COUNT(*) FROM community_logs l WHERE LOGS_TYPE = 0 AND l.RECORD_NO = r.RECORD_NO) AS logs_count FROM record r
shared_date IS NOT NULL AND temp_type=1 AND timecapsule_type=0
AND (
r.record_title LIKE #{searchKeyword}
r.record_text LIKE #{searchKeyword}
)
AND r.category_no=#{categoryNo}
AND NOT EXISTS ( SELECT 1 FROM follow_block fb WHERE fb.user_id = #{userId} AND fb.target_id = r.RECORD_USER_ID AND fb.fb_type = 1 )
ORDER BY shared_date DESC, shared_date IS NULL DESC

mapper.select:
SELECT
inner_table.*
FROM
	(

	) inner_table
LIMIT #{limit} OFFSET #{offset}

SELECT a1.record_no AS record_no, a1.record_user_id AS record_user_id, a4.nickname AS nickname, a4.profile_image_name AS profile_image_name, a1.record_title AS record_title, a1.latitude AS latitude, a1.longitude AS longitude, a1.checkpoint_address AS checkpoint_address, DATE_FORMAT(a1.checkpoint_date, '%Y-%m-%d %H:%i:%s') AS checkpoint_date, a1.media_name AS media_name, a1.category_no AS category_no, a3.category_name AS category_name, a3.category_imoji AS category_imoji, a1.record_text AS record_text, a1.temp_type AS temp_type, DATE_FORMAT(a1.record_add_date, '%Y-%m-%d %H:%i:%s') AS record_add_date, DATE_FORMAT(a1.shared_date, '%Y-%m-%d %H:%i:%s') AS shared_date, a1.update_count AS update_count, a1.d_day_date AS d_day_date, a1.timecapsule_type AS timecapsule_type FROM record a1 LEFT JOIN category a3 ON a1.category_no = a3.category_no LEFT JOIN users a4 ON a1.record_user_id = a4.user_id
a1.latitude BETWEEN #{minLatitude} AND #{maxLatitude} AND a1.longitude BETWEEN #{minLongitude} AND #{maxLongitude} AND a1.temp_type=1 AND a1.timecapsule_type=0
OR( a1.latitude BETWEEN #{minLatitude} AND #{maxLatitude} AND a1.longitude BETWEEN #{minLongitude} AND #{maxLongitude} AND a1.record_user_id IN ( SELECT target_id FROM follow_block WHERE user_id=#{userId} AND FB_TYPE=0 ) AND a1.shared_date IS NOT NULL AND a1.temp_type=1 AND a1.timecapsule_type=0
AND (a1.record_text LIKE #{searchKeyword} OR a1.record_title LIKE #{searchKeyword} OR a1.record_no IN ( SELECT record_no FROM image_tag WHERE IMAGE_TAG_TYPE = 0 AND image_tag_text LIKE #{searchKeyword}))
AND a1.category_no=#{categoryNo}
)
OR ( a1.latitude BETWEEN #{minLatitude} AND #{maxLatitude} AND a1.longitude BETWEEN #{minLongitude} AND #{maxLongitude} AND a1.shared_date IS NOT NULL AND NOT a1.record_user_id=#{userId} AND NOT a1.record_user_id IN ( SELECT target_id FROM follow_block WHERE user_id=#{userId} AND FB_TYPE=0 ) AND a1.temp_type=1 AND a1.timecapsule_type=0
AND (a1.record_text LIKE #{searchKeyword} OR a1.record_title LIKE #{searchKeyword} OR a1.record_no IN ( SELECT record_no FROM image_tag WHERE IMAGE_TAG_TYPE = 0 AND image_tag_text LIKE #{searchKeyword}))
AND a1.category_no=#{categoryNo}
)
OR ( a1.latitude BETWEEN #{minLatitude} AND #{maxLatitude} AND a1.longitude BETWEEN #{minLongitude} AND #{maxLongitude} AND a1.record_user_id=#{userId} AND a1.temp_type=1 AND a1.timecapsule_type=0
AND (a1.record_text LIKE #{searchKeyword} OR a1.record_title LIKE #{searchKeyword} OR a1.record_no IN ( SELECT record_no FROM image_tag WHERE IMAGE_TAG_TYPE = 0 AND image_tag_text LIKE #{searchKeyword}))
AND a1.category_no=#{categoryNo}
)
ORDER BY rand() LIMIT #{limit}


SELECT COUNT(*) AS profileRecord_Count
FROM() AS inner_table 
GROUP BY record_user_id=#{userId}
